CREATE TABLE `question` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '试题编号',
  `filetype` int(11) NOT NULL,
  `type` int(11) NOT NULL COMMENT '试题类型',
  `group` int(11) NOT NULL,
